sqlMap.procedure:
DECLARE
I_COUNT NUMBER;
I_SEQ VARCHAR2(10) := #categorySeq#;
BEGIN
	SELECT COUNT(CATEGORY_SEQ) INTO I_COUNT FROM I_BOARD_CATEGORY WHERE CATEGORY_SEQ = I_SEQ;
	BEGIN
	IF I_COUNT <> 0 THEN
		UPDATE I_BOARD_CATEGORY SET
			CATEGORY_NAME =  #categoryName#,
			USE_YN = #useYn#,
			MOD_DATE = SYSDATE
		WHERE CATEGORY_SEQ = I_SEQ;
	ELSE
		INSERT INTO I_BOARD_CATEGORY (
			CONFIG_SEQ,
			CATEGORY_SEQ,
			CATEGORY_NAME,
			USE_YN,
			ORDER_NUM
		) VALUES(
			#configSeq#,
			I_SEQ,
			#categoryName#,
			#useYn#,
			#orderNum#
		);
	END IF;
END;
END;
